import * as userApi from '@app/api/user';

export async function loginIsValid(token) {
    if (!token) {
        return false;
    }
    try {
        const response = await userApi.checkAccess(token);
        if (response.success) {
            return true;
        }
    } catch (error) {
        console.error('Login Invalid: ', token);
    }
    return false;
}
