import * as api from '@app/api'

const urlPrefix = 'group';

export function list(data, token) {
    return api.get(`${urlPrefix}`, data, token);
}

export function getGroupList(token) {
    return api.get(`${urlPrefix}`, {
        fields: 'id,name,test_status',
        sort: 'display_name',
        'per-page': 500
    }, token);
}

export function getStarred(token) {
    return api.get(`${urlPrefix}/starred`, {}, token);
}

export function star(id, token) {
    return api.put(`${urlPrefix}/star/${id}`, {}, token);
}

export function unstar(id, token) {
    return api.put(`${urlPrefix}/unstar/${id}`, {}, token);
}

export function view(id, data, token) {
    return api.get(`${urlPrefix}/${id}`, data, token);
}

export function create(data, token) {
    return api.post(`${urlPrefix}`, data, token);
}

export function update(id, data, token) {
    return api.put(`${urlPrefix}/${id}`, data, token);
}

export function remove(id, token) {
    return api.del(`${urlPrefix}/${id}`, token);
}